{
    DEBUGMSGTL(("internal:serialTable:serialTable_release_rowreq_ctx","called\n"));

    netsnmp_assert(NULL != rowreq_ctx);
    
    serialTable_rowreq_ctx_cleanup(rowreq_ctx);

    if(rowreq_ctx->undo)
        serialTable_release_data(rowreq_ctx->undo);
 
    /*
     * free index oid pointer
     */
    if(rowreq_ctx->oid_idx.oids != rowreq_ctx->oid_tmp)
        free(rowreq_ctx->oid_idx.oids);

    SNMP_FREE(rowreq_ctx);
} /* serialTable_release_rowreq_ctx */

