int
_mfd_serialTable_irreversible_commit(netsnmp_mib_handler *handler,
                         netsnmp_handler_registration *reginfo,
                         netsnmp_agent_request_info *agtreq_info,
                         netsnmp_request_info *requests)
{
    serialTable_rowreq_ctx *rowreq_ctx =
                  netsnmp_container_table_row_extract(requests);
    
    DEBUGMSGTL(("internal:serialTable:_mfd_serialTable_irreversible:commit","called\n"));
   
    netsnmp_assert(NULL != rowreq_ctx);
    
    /*
     * check for and handle row creation/deletion
     * and update column exist flags...
     */
    if (rowreq_ctx->rowreq_flags & MFD_ROW_DELETED) {
        if (! (rowreq_ctx->rowreq_flags & MFD_ROW_CREATED))
            CONTAINER_REMOVE(serialTable_if_ctx.container, rowreq_ctx);
    }
    else {
        if (rowreq_ctx->column_set_flags) {
           rowreq_ctx->column_set_flags = 0;
        }
        if (rowreq_ctx->rowreq_flags & MFD_ROW_CREATED) {
            rowreq_ctx->rowreq_flags &= ~MFD_ROW_CREATED;
            CONTAINER_INSERT(serialTable_if_ctx.container, rowreq_ctx);
        }
    }

    return SNMP_ERR_NOERROR;
} /* _mfd_serialTable_irreversible_commit */

/***********************************************************************
 *
 * DATA ACCESS
 *
 ***********************************************************************/
/**
 * @internal
 */
static void
_container_item_free(serialTable_rowreq_ctx *rowreq_ctx, void *context)
{
    DEBUGMSGTL(("internal:serialTable:_container_item_free","called\n"));

    if(NULL == rowreq_ctx)
        return;

    serialTable_release_rowreq_ctx(rowreq_ctx);
} /* _container_item_free */

/**
 * @internal
 */
static void
_container_free(netsnmp_container *container)
{
    DEBUGMSGTL(("internal:serialTable:_container_free","called\n"));

    if (NULL == container) {
        snmp_log(LOG_ERR, "invalid container in serialTable_container_free\n");
        return;
    }

    /*
     * call user code
     */
    serialTable_container_free(container);
    
    /*
     * free all items. inefficient, but easy.
     */
    CONTAINER_CLEAR(container,
                    (netsnmp_container_obj_func *)_container_item_free,
                    NULL);
} /* _container_free */

/**
 * @internal
 * initialize the container with functions or wrappers
 */
void
_serialTable_container_init(serialTable_interface_ctx *if_ctx)
{
    DEBUGMSGTL(("internal:serialTable:_serialTable_container_init","called\n"));

    /*
     * container init
     */    
    serialTable_container_init(&if_ctx->container);
    if(NULL == if_ctx->container)
        if_ctx->container = netsnmp_container_find("serialTable:table_container");
    if(NULL == if_ctx->container) {
        snmp_log(LOG_ERR,"error creating container in "
                 "serialTable_container_init\n");
        return;
    }

} /* _serialTable_container_init */

/**
 * @internal
 * shutdown the container with functions or wrappers
 */
void
_serialTable_container_shutdown(serialTable_interface_ctx *if_ctx)
{
    DEBUGMSGTL(("internal:serialTable:_serialTable_container_shutdown","called\n"));

    serialTable_container_shutdown(if_ctx->container);

    _container_free(if_ctx->container);

} /* _serialTable_container_shutdown */


serialTable_rowreq_ctx *
serialTable_row_find_by_mib_index(serialTable_mib_index *mib_idx)
{
    serialTable_rowreq_ctx   *rowreq_ctx;
    oid                      oid_tmp[MAX_OID_LEN];
    netsnmp_index            oid_idx;
    int                      rc;

    /*
     * set up storage for OID
     */
    oid_idx.oids = oid_tmp;
    oid_idx.len = sizeof(oid_tmp)/sizeof(oid);

    /*
     * convert
     */
    rc = serialTable_index_to_oid(&oid_idx, mib_idx);
    if (MFD_SUCCESS != rc)
        return NULL;

    rowreq_ctx = CONTAINER_FIND(serialTable_if_ctx.container, &oid_idx);

    return rowreq_ctx;
}

